import React, { useState } from 'react';
import { SafeAreaView, ScrollView, Text, View } from 'react-native';
import tw from 'twrnc';
import CategoryPills from '../../../components/discover/CategoryPills';
import DestinationCard from '../../../components/discover/DestinationCard';
import Header from '../../../components/discover/Header';
import Search from '../../../components/discover/Search';

interface Category {
  id: number;
  name: string;
  icon: string;
}

interface Destination {
  id: number;
  name: string;
  image: string;
  isOngoing: boolean;
  activities: number;
  participants: number;
  categoryId: number;
}

const destinationsData: Destination[] = [
  {
    id: 1,
    name: 'Labadi Beach, Accra, Ghana',
    image: 'data:image/jpeg;base64,',
    isOngoing: true,
    activities: 15,
    participants: 8,
    categoryId: 2 // Beach
  },
  {
    id: 2,
    name: 'Kakum National Park, Cape Coast, Ghana',
    image: 'data:image/jpeg;base64,',
    isOngoing: false,
    activities: 10,
    participants: 12,
    categoryId: 3 // Adventure
  },
  {
    id: 3,
    name: 'Ada Foah, Greater Accra, Ghana',
    image: '/api/placeholder/400/320',
    isOngoing: true,
    activities: 8,
    participants: 5,
    categoryId: 4 // Relax
  },
  {
    id: 4,
    name: 'Mole National Park, Northern Region, Ghana',
    image: '/api/placeholder/400/320',
    isOngoing: false,
    activities: 20,
    participants: 10,
    categoryId: 3 // Adventure
  }
];

const categoriesData: Category[] = [
  { id: 1, name: 'For You', icon: '👤' },
  { id: 2, name: 'Beach', icon: '🏖️' },
  { id: 3, name: 'Adventure', icon: '🏔️' },
  { id: 4, name: 'Relax', icon: '🧘' },
];

const Discover: React.FC = () => {
  const [activeCategory, setActiveCategory] = useState<number>(1);
  const [filteredCategories] = useState<Category[]>(categoriesData);

  const handleCategoryChange = (categoryId: number) => {
    setActiveCategory(categoryId);
  };

  const handleSearch = () => {
    console.log('Searching nearby...');
  };

  // Filter destinations based on active category
  const filteredDestinations = activeCategory === 1
    ? destinationsData
    : destinationsData.filter(destination => destination.categoryId === activeCategory);

  return (
    <SafeAreaView style={tw`bg-white flex-1`}>
      <Header />
      <ScrollView>
        <View style={tw`mt-4`}>
          <Search location="Ghana" onSearch={handleSearch} />
          <CategoryPills 
            categories={filteredCategories} 
            initialActiveId={1}
            onCategoryChange={handleCategoryChange}
          />
          {filteredDestinations.length > 0 ? (
            filteredDestinations.map(destination => (
              <DestinationCard key={destination.id} destination={destination} />
            ))
          ) : (
            <Text style={tw`text-black text-center mt-4`}>No destinations found for this category.</Text>
          )}
        </View>
      </ScrollView>
    </SafeAreaView>
  );
};

export default Discover;